-- Create favorite places table
CREATE TABLE favorite_places (
  id UUID PRIMARY KEY DEFAULT gen_random_uuid(),
  user_id UUID REFERENCES auth.users(id) ON DELETE CASCADE,
  place_id TEXT NOT NULL,
  place_name TEXT NOT NULL,
  place_description TEXT,
  place_category TEXT,
  place_location TEXT,
  place_image_url TEXT,
  created_at TIMESTAMP WITH TIME ZONE DEFAULT NOW()
);

-- Create recent searches table
CREATE TABLE recent_searches (
  id UUID PRIMARY KEY DEFAULT gen_random_uuid(),
  user_id UUID REFERENCES auth.users(id) ON DELETE CASCADE,
  search_query JSONB NOT NULL,
  search_results JSONB,
  created_at TIMESTAMP WITH TIME ZONE DEFAULT NOW()
);

-- Create reviews table
CREATE TABLE reviews (
  id UUID PRIMARY KEY DEFAULT gen_random_uuid(),
  user_id UUID REFERENCES auth.users(id) ON DELETE CASCADE,
  place_id TEXT NOT NULL,
  place_name TEXT NOT NULL,
  rating INTEGER CHECK (rating >= 1 AND rating <= 5),
  review_text TEXT,
  created_at TIMESTAMP WITH TIME ZONE DEFAULT NOW(),
  updated_at TIMESTAMP WITH TIME ZONE DEFAULT NOW()
);

-- Create user settings table
CREATE TABLE user_settings (
  id UUID PRIMARY KEY DEFAULT gen_random_uuid(),
  user_id UUID REFERENCES auth.users(id) ON DELETE CASCADE UNIQUE,
  theme TEXT DEFAULT 'system' CHECK (theme IN ('light', 'dark', 'system')),
  notifications_enabled BOOLEAN DEFAULT true,
  location_sharing BOOLEAN DEFAULT true,
  preferred_budget TEXT DEFAULT 'moderate' CHECK (preferred_budget IN ('budget', 'moderate', 'premium')),
  created_at TIMESTAMP WITH TIME ZONE DEFAULT NOW(),
  updated_at TIMESTAMP WITH TIME ZONE DEFAULT NOW()
);

-- Enable RLS on all tables
ALTER TABLE favorite_places ENABLE ROW LEVEL SECURITY;
ALTER TABLE recent_searches ENABLE ROW LEVEL SECURITY;
ALTER TABLE reviews ENABLE ROW LEVEL SECURITY;
ALTER TABLE user_settings ENABLE ROW LEVEL SECURITY;

-- Create RLS policies for favorite_places
CREATE POLICY "Users can view their own favorite places" ON favorite_places
  FOR SELECT USING (user_id = auth.uid());

CREATE POLICY "Users can insert their own favorite places" ON favorite_places
  FOR INSERT WITH CHECK (user_id = auth.uid());

CREATE POLICY "Users can delete their own favorite places" ON favorite_places
  FOR DELETE USING (user_id = auth.uid());

-- Create RLS policies for recent_searches
CREATE POLICY "Users can view their own recent searches" ON recent_searches
  FOR SELECT USING (user_id = auth.uid());

CREATE POLICY "Users can insert their own recent searches" ON recent_searches
  FOR INSERT WITH CHECK (user_id = auth.uid());

CREATE POLICY "Users can delete their own recent searches" ON recent_searches
  FOR DELETE USING (user_id = auth.uid());

-- Create RLS policies for reviews
CREATE POLICY "Users can view all reviews" ON reviews
  FOR SELECT TO authenticated USING (true);

CREATE POLICY "Users can insert their own reviews" ON reviews
  FOR INSERT WITH CHECK (user_id = auth.uid());

CREATE POLICY "Users can update their own reviews" ON reviews
  FOR UPDATE USING (user_id = auth.uid());

CREATE POLICY "Users can delete their own reviews" ON reviews
  FOR DELETE USING (user_id = auth.uid());

-- Create RLS policies for user_settings
CREATE POLICY "Users can view their own settings" ON user_settings
  FOR SELECT USING (user_id = auth.uid());

CREATE POLICY "Users can insert their own settings" ON user_settings
  FOR INSERT WITH CHECK (user_id = auth.uid());

CREATE POLICY "Users can update their own settings" ON user_settings
  FOR UPDATE USING (user_id = auth.uid());

-- Create indexes for better performance
CREATE INDEX idx_favorite_places_user_id ON favorite_places(user_id);
CREATE INDEX idx_recent_searches_user_id ON recent_searches(user_id);
CREATE INDEX idx_reviews_user_id ON reviews(user_id);
CREATE INDEX idx_reviews_place_id ON reviews(place_id);
CREATE INDEX idx_user_settings_user_id ON user_settings(user_id);
